import React, { Component } from 'react'
import {
    Button,
    Checkbox,
    Form,
    Input,
    Radio,
    Select,
    TextArea,
} from 'semantic-ui-react'

const options = [
    { key: 'm', text: 'Male', value: 'male' },
    { key: 'f', text: 'Female', value: 'female' },
    { key: 'o', text: 'Other', value: 'other' },
]

export class MatchesForm extends Component {
    state = {}

    handleChange = (e, { value }) => this.setState({ value })

    render() {
        const { value } = this.state
        return (
            <Form>
                <Form.Group widths='equal'>
                    <Form.Field
                        control={Input}
                        label='First name'
                        placeholder='First name'
                    />
                    <Form.Field
                        control={Input}
                        label='Last name'
                        placeholder='Last name'
                    />
                    <Form.Field
                        control={Select}
                        label='Gender'
                        options={options}
                        placeholder='Gender'
                    />
                </Form.Group>
                <Form.Group inline>
                    <label>Quantity</label>
                    <Form.Field
                        control={Radio}
                        label='One'
                        value='1'
                        checked={value === '1'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Two'
                        value='2'
                        checked={value === '2'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        control={Radio}
                        label='Three'
                        value='3'
                        checked={value === '3'}
                        onChange={this.handleChange}
                    />
                </Form.Group>
                <Form.Field
                    control={TextArea}
                    label='Notes'
                    placeholder='Think and record the significant moments of this match and what would make it '
                />
                <Form.Field control={Button}>Submit</Form.Field>
            </Form>
        )
    }
}

export default MatchesForm
