import React, { Component } from 'react';
import { connect } from "react-redux";
import { addMatch } from "../actions/MatchActions";
import uuid from 'uuid'

import {
    Button,
    Header,
    Form,
    Input,
    Radio,
    TextArea,
} from 'semantic-ui-react'

class MatchesForm extends Component {
    constructor () {
        super();
        this.state = {
            opponent_name: "",
            match_type: "",
            notes: "",
        }   
    }

    handleOnChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
        console.log(this.state)
    }

    handleChecked = (e, { value }) => this.setState({ match_type: value })

    handleSubmit = event => {
        event.preventDefault();
        debugger
        this.props.addMatch({ ...this.state, id: uuid() });
        this.setState({
            date: "",
            opponent_name: "",
            match_type: "",
            notes: "",
            checked: false
        });

        console.log("Submit")
    }

    render() {
        // const { value } = this.state
        console.log("MatchesForm.js", this.state, this.props)
        return (
            <Form onSubmit={ event => this.handleSubmit(event)}>
                <Header as='h3' block textAlign='center' color='blue'>
                    Create a Match
                </Header>
                <Form.Group widths='equal'>
                    <label>Date:</label><br>
                    </br>
                    <div className="ui input left icon">
                        <i className="calendar icon"></i>
                        <input
                            type="date" 
                            placeholder="Date"
                            name="date"
                            onChange={this.handleOnChange}
                            // Trying to make this a controlled value creates an error. 
                            // value={this.state.date}
                        />
                    </div>



                    <Form.Field
                        name="opponent_name"
                        control={Input}
                        label='Opponent Name:'
                        placeholder='Opponent Name'
                        value={this.state.opponent_name}
                        onChange={this.handleOnChange}
                    />

                </Form.Group>
                <Form.Group inline>
                    <label>Match Type:</label>
                    <Form.Field
                        name="match_type"
                        control={Radio}
                        label='Best of 7'
                        value='7'
                        onChange={this.handleChecked}
                        checked={this.state.radioChecked}
                    />
                    <Form.Field
                        name="match_type"
                        control={Radio}
                        label='Best of 5'
                        value='5'
                        onChange={this.handleChecked}
                        onClick={() => this.setState({checked: true})}
                    />
                </Form.Group>

                <Form.Field
                    name="notes"
                    control={TextArea}
                    label='Notes:'
                    placeholder='Notes'
                    value={this.state.notes}
                    onChange={this.handleOnChange}
                />
                <Form.Field 
                    control={Button}>
                    Submit
                </Form.Field>
            </Form>
        )
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        addMatch: (formData) => dispatch(addMatch(formData))
    };
};

export default connect(null, mapDispatchToProps)(MatchesForm);
