import React from 'react';
import { connect } from 'react-redux';
import { updateLoginForm } from "../actions/LoginFormActions";

function handleSubmit () {

}

// function handleOnChange () {

// }

const login = ({username, password}) => {
    return (
        <form onSubmit={handleSubmit}>
            <label>Username:</label> <br/>
            <input name="username" type='text' value={username} onchange={event => updateLoginForm} /><br/>

            <label>Password:</label><br/>
            <input name="password" label="password" type='text' value={password} onchange={event => updateLoginForm} /><br/>
            <input type="submit" value="log in" />
        </form>
    )
}

const mapStateToProps = ({ username, password }) => ({ username, password });

export default connect(mapStateToProps, { updateLoginForm })(login)

