// Libraries:
import React, { Component } from 'react';
import { connect } from "react-redux";
// Components:
import { Header, Grid, Image, Segment, Divider } from 'semantic-ui-react';
import MatchesForm from '../components/MatchesForm';
import MatchesHeader from '../components/MatchesHeader';
import MatchCard from '../components/MatchCard'
// Functions:
import { addMatch, removeMatch } from "../actions/MatchActions";


class Dashboard extends Component {
    render() {
        console.log("containers/dashboard.js", this.props.players, this.props.matches)
        // debugger
        const player = this.props.players[0]
        const matches = this.props.matches
        return(
            <div>   
                <Grid stackable columns={2} divided>
                    <Grid.Column textAlign='center'>
                        <Segment style={{height: "33vh"}}>
                            <Header as='h3' block textAlign='center' color='blue'>
                            Profile
                            </Header>
                            <Image bordered rounded size='large' src={ player.profileImage} style={{width: '200px', height: "auto"}}centered />
                            <h5>{player.username}</h5>
                            <p>Win : Loss</p>
                            <span>{player.wins} : {player.losses} </span>          
                        </Segment>
                    </Grid.Column>

                    <Grid.Column textAlign='left'>
                        <Segment style={{ height: "33vh" }} >
                            {/* CALLING MATCHESFORM */}
                            <MatchesForm 
                                addMatch={ this.props.addMatch }/>
                        </Segment>
                    </Grid.Column>
                </Grid>
                <Divider />
                {MatchesHeader()}
                <Grid divided='vertically'>
                    <Grid.Row columns={3}>
                        {/* CALLING MATCHCARD */}
                        {matches.map(m =>
                            <MatchCard match={m} />
                            )
                        }
                    </Grid.Row>
                </Grid>
            </div>
        )
    }
}

// ES6 destructured syntax.
const mapStateToProps = ({players, matches}) => ({
    players,
    matches
})
// Longhand version:
// const mapStateToProps = (state) => ({
//      players: state,
//      matches: 
// })

const mapDispatchToProps = (dispatch) => ({
    addMatch: () => dispatch(addMatch(matchId)),
    removeMatch: () => dispatch(removeMatch(matchId))
})


// ES6 syntax below:
export default connect(mapStateToProps, {addMatch, removeMatch})(Dashboard)
