console.log("reducers/PlayersReducer.js")

// Sets an initial state for a Player and will be overwritten by fetch within Dashboard.js

export default (state = 
    {
        "player": {
            "id": 1,
            "username": "Baron Lip",
            "profileImage": "/images/Bitmoji_Baron_lr.png",
            "wins": 0,
            "losses": 0,
            "created_at": "2020-01-05T05:22:18.750Z",
            "updated_at": "2020-01-05T05:22:18.750Z"
        },
        "matches": [
            {
                "id": 1,
                "date": "2019-12-26",
                "opponent_name": "Chad",
                "match_type": "5",
                "notes": "His serves are illusive, watch the spin carefully.",
                "bookmarked": false,
                "player_id": 1,
                "created_at": "2020-01-05T05:22:18.783Z",
                "updated_at": "2020-01-05T05:22:18.783Z"
            },
            {
                "id": 2,
                "date": "2019-12-20",
                "opponent_name": "Scott",
                "match_type": "7",
                "notes": "Close the angle more against his slow loops.",
                "bookmarked": false,
                "player_id": 1,
                "created_at": "2020-01-05T05:22:18.814Z",
                "updated_at": "2020-01-05T05:22:18.814Z"
            },
            {
                "id": 3,
                "date": "2020-01-01",
                "opponent_name": "Ryan",
                "match_type": "5",
                "notes": "He's getting me everytime.",
                "bookmarked": false,
                "player_id": 1,
                "created_at": "2020-01-05T05:26:29.734Z",
                "updated_at": "2020-01-05T05:26:29.734Z"
            }
        ]
    }
    , action) => {

    switch (action.type) {

        case "FETCH_PLAYER":
            console.log("You're fetching a player", state, action);
            debugger
            return [...state, action.player];

        case "SHOW_PLAYER":
            console.log("You're showing a player", state, action);
            // debugger
            return [state[0] = action.player]

        case "ADD_PLAYER":
            console.log("You're adding a player", state, action);
            // debugger
            return [...state, action.player];

        default:
            return state;
    }
}
