                    <Grid.Column>
                        <Segment>

                        </Segment>
                    </Grid.Column>
                </Grid>
                <Divider />
                <Grid stackable columns={3} divided>

                </Grid>
            </div>
        )
    }
}

// ES6 destructured syntax.
const mapStateToProps = ({players}) => ({
    players
})
