import React, { Component } from 'react';
import { connect } from "react-redux";

import {
    Button,
    Header,
    Form,
    Input,
    Radio,
    TextArea,
} from 'semantic-ui-react'

export class MatchesForm extends Component {
    state = {}

    handleOnChange = event => {
        // Using object destructuring, you can pull out values from an object using a key value of the same name.
        // With the HTML node's value attribute set to equal the key within state, referring to the value attribute will refer to the right key in state.
        const { name, value } = event.target;
        // This dynamically fills in the state info based on the input fields.
        this.setState({
            [name]: value
        });
        // console.log(this.state)
    }
    // handleChange = (e, { value }) => this.setState({ value })

    render() {
        const { value } = this.state
        console.log("You've reached MatchesForm", this.state)
        return (
            <Form>
                <Header as='h3' block textAlign='center' color='blue'>
                    Create a Match
                </Header>

                <Form.Group widths='equal'>
                    <Form.Field
                        name="opponent_name"
                        control={Input}
                        label='Opponent Name'
                        placeholder='Opponent Name'
                        value={this.state.opponent_name}
                    />
                </Form.Group>

                <Form.Group inline>
                    <label>Match Type</label>
                    <Form.Field
                        name="match_type"
                        control={Radio}
                        label='Best of 7'
                        value='7'
                        checked={value === '7'}
                        onChange={this.handleChange}
                    />
                    <Form.Field
                        name="match_type"
                        control={Radio}
                        label='Best of 5'
                        value='5'
                        checked={value === '5'}
                        onChange={this.handleChange}
                    />
                </Form.Group>
                <Form.Field
                    name="notes"
                    control={TextArea}
                    label='Notes'
                    placeholder='Think and record the significant moments of this match and what you want to remember.'
                    value={this.state.content}
                />
                <Form.Field control={Button}>Submit</Form.Field>
            </Form>
        )
    }
}

const mapStateToProps = (state) => ({
    state
})

export default connect(mapStateToProps)(MatchesForm)
