console.log("reducers/PlayersReducer.js")

export default (state = 
    [
        {   
            username: "",
            profileImage: "",
            wins: 0,
            losses: 0,
            // matches: [
            // ]
        }
    ]
    , action) => {


    switch (action.type) {

        case "INIT_FETCH_PLAYER":
            console.log("Fetching initial player", state, action);
            // debugger
            return [...state];

        case "FETCH_PLAYER":
            console.log("You're fetching a player", state, action);
            // debugger
            return [...state];

        case "ADD_PLAYER":
            console.log("You're adding a player", state, action);
            // debugger
            return [...state, action.player];

        default:
            return state;
    }
}
