import React, { Component } from 'react';
import { connect } from "react-redux";
import { createMatch } from "../actions/MatchActions";

import {
    Button,
    Header,
    Form,
    Input,
    Radio,
    TextArea,
    Grid,
    Divider,
    Segment
} from 'semantic-ui-react'


class MatchesForm extends Component {
    constructor () {
        super();
        this.state = {
            opponent_name: "",
            match_type: "",
            notes: "",
            bookmarked: null,
            games: []
        }   
    }

    handleOnChange = event => {
        const { name, value } = event.target;
        this.setState({
            [name]: value
        });
    }

    handleChecked = (e, { value }) => {
        this.setState({ match_type: value })
    }

    handleOnChangeForGames = event => {
        let { name, value } = event.target;
        debugger
        value = parseInt(value, 10);
        this.setState( {...this.state, games: { ...this.state.games, [name]: value } } );
    }


    handleSubmit = event => {
        console.log("Submitting from MatchesForm.", this.props.player);
        event.preventDefault();
        // debugger
        this.props.createMatch({...this.state, bookmarked: false }, this.props.player);
        // // Reset the form below.
        this.setState({
            date: "",
            opponent_name: "",
            match_type: "",
            notes: "",
            bookmarked: null,
            games: [ ]
        });
    }

    render() {
        console.log("MatchesForm.js", this.state, this.props)

        let matchType = this.state.match_type;
        let gameForm;

        if (matchType === "5") {
            gameForm = 
            <>
                <Segment>
                    <Grid columns='equal' relaxed='not'>
                        <Grid.Row>
                            <Grid.Column>
                                <Header as='h4' textAlign="centered">Game 1</Header>
                            </Grid.Column>
                            <Grid.Column>
                                <Input
                                    placeholder='you'
                                    name="player_score"
                                    // value={this.state.match}
                                    onChange={this.handleOnChangeForGames}
                                />
                            </Grid.Column>

                            <Grid.Column>
                                <Input
                                    name="opponent_score"
                                    placeholder='opponent'
                                    // value={this.state.opponent_name}
                                    onChange={this.handleOnChangeForGames}
                                />
                            </Grid.Column>
                            <Grid.Column>
                                <Button>Save</Button>
                            </Grid.Column>
                        </Grid.Row>
                            <Divider vertical>:</Divider>
                    </Grid>
                </Segment>
                <Form.Group widths='equal'>
                    <Form.Field inline>
                        <label>Game 2:</label>
                        <Input
                            placeholder='your score'
                            name="player_score"
                            // value={this.state.match}
                            onChange={this.handleOnChangeForGames}
                        />
                        <label> : </label>
                        <Input
                            name="opponent_score"
                            placeholder='opponent score'
                            // value={this.state.opponent_name}
