import React from 'react';
import { connect } from 'react-redux';
import { updateLoginForm } from "../actions/LoginFormActions";

function handleSubmit () {

}

function handleOnChange () {

}


const login = (props) => {
    return (
        <form onSubmit={this.handleSubmit}>
            <label>Username:</label> <br/>
            <input name="username" type='text' value={props.username} onchange={updateLoginForm} /><br/>

            <label>Password:</label><br/>
            <input name="password" label="password" type='text' value={props.password} onchange={updateLoginForm} /><br/>
            <input type="submit" value="log in" />
        </form>
    )
}

const mapStateToProps = ({ username, password }) => ({ username, password });

