export default (state = [], action) => {

    console.log("reducers/MatchesReducer.js")

    switch (action.type) {
        case "ADD_MATCH":
            console.log("You're adding a match", state, action);
            return [...state, action.player];

        case "REMOVE_MATCH":
            console.log("You're removing a match", state, action)
            return []

            case "STAR_MATCH":

        default:
            return state;
    }
}
