import uuid from 'uuid';

export default (state = {
    matches: []
    }, action) => {

    console.log("reducers/MatchesReducer.js")

    switch (action.type) {
        case "ADD_MATCH":
            console.log("You're adding a match", state, action);
            const match = {
                id: uuid(),
                ...action.match
            };
            // debugger;
            return { ...state, matches: [...state.matches, match ] };

        case "REMOVE_MATCH":
            console.log("You're removing a match", state, action);
            debugger;
            return state.matches.filter((match) => match.id !== action.payload)

        case "STAR_MATCH":
            console.log("You're staring a match", state, action)
            return

        default:
            return state;
    }
}
